import React from 'react';
import image from '../Media/portrait-gorgeous-hispanic-preschool-teacher-teaching-her-students-classroom.png';
import { Box, Button, Stack, Typography } from '@mui/material';
import Carousel from 'react-material-ui-carousel';

export default function Sliders() {
  const items = [
    {
      title: "Make the best out of education through our Learning Approaches!",
      description: "Whether you’re a student looking to get tutored, or a parent who’s looking to hone their kid’s skills in learning; you’re in the right place! We’ll provide you with the best tutors while also teaching you how to properly learn!",
    },
    {
      title: "Another Slide",
      description: "This is another slide in the carousel. You can add as many slides as you want.",
    },
  ];

  const renderCarouselItem = (item, index) => (
    <Stack
      key={index}
      direction="row"
      gap={7}
      sx={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', alignItems: 'center' }}
    >
      <Stack>
        <Typography sx={{ fontSize: '40px', fontWeight: 'bold', color: '#333134' }}>
          {item.title}
        </Typography>
        <Typography sx={{ color: '#333134', fontSize: '15px' }}>
          {item.description}
        </Typography>
        <Button
          sx={{
            bgcolor: '#018EA2',
            color: 'white',
            width: '200px',
            marginTop: '20px',
            '&:hover': { bgcolor: '#018EA2', color: 'white' },
          }}
        >
          Yes, I want better learning!
        </Button>
      </Stack>
      <img src={image} style={{ objectFit: 'fill', width: '350px', height: '350px' }} />
    </Stack>
  );

  return (
    <Carousel
      indicators={false}
      navButtonsProps={{
        style: {
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          borderRadius: '50%',
          width: '10px',
          height: '10px',
          margin: '0 5px',
        },
      }}
    >
      {items.map((item, index) => renderCarouselItem(item, index))}
    </Carousel>
  );
}
