import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import image from '../Media/portrait-gorgeous-hispanic-preschool-teacher-teaching-her-students-classroom.png';
import { Box, Button, Stack, Typography } from '@mui/material';

export default function Sliders() {
    const items = [
        {
          title: "Make the best out of education through our Learning Approaches!",
          description: "Whether you’re a student looking to get tutored, or a parent who’s looking to hone their kid’s skills in learning; you’re in the right place! We’ll provide you with the best tutors while also teaching you how to properly learn!",
        },
        {
          title: "Another Slide",
          description: "This is another slide in the carousel. You can add as many slides as you want.",
        },
      ];
  return (
    <Carousel
      showThumbs={false}
      showStatus={false}
      renderIndicator={(onClickHandler, isSelected, index, label) => (
        <button
          type="button"
          style={{
            border: 'none',
            width: '10px',
            height: '10px',
            borderRadius: '50%',
            background: isSelected ? 'black' : 'gray',
            margin: '0 5px',
            cursor: 'pointer',
          }}
          onClick={onClickHandler}
        />
      )}
    >
      <div>
        {items.map((item)=>
        <Stack
          direction="row"
          gap={7}
          sx={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', alignItems: 'center' }}
        >
          <Stack>
            <Typography sx={{ fontSize: '40px', fontWeight: 'bold', color: '#333134' }}>
              Make the best <br />
              out of education<br />
              through our Learning<br />
              Approaches!
            </Typography>
            <Typography sx={{ color: '#333134', fontSize: '15px' }}>
              Whether you’re a student looking to get tutored, <br />
              or a parent who’s looking to hone their kid’s skills in learning; you’re in the right place!<br />
              We’ll provide you with best tutors while also teaching you how to properly learn!
            </Typography>
            <Button
              sx={{
                bgcolor: '#018EA2',
                color: 'white',
                width: '200px',
                marginTop: '20px',
                '&:hover': { bgcolor: '#018EA2', color: 'white' },
              }}
            >
              Yes, I want better learning!
            </Button>
          </Stack>
          <img src={image} style={{ objectFit: 'fill', width: '350px', height: '350px' }} />
        </Stack>
        )}
      </div>

      <div>
        <Stack
          direction="row"
          gap={7}
          sx={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', alignItems: 'center' }}
        >
          <Stack>
            <Typography sx={{ fontSize: '40px', fontWeight: 'bold', color: '#333134' }}>
              Make the best <br />
              out of education<br />
              through our Learning<br />
              Approaches!
            </Typography>
            <Typography sx={{ color: '#333134', fontSize: '15px' }}>
              Whether you’re a student looking to get tutored, <br />
              or a parent who’s looking to hone their kid’s skills in learning; you’re in the right place!<br />
              We’ll provide you with best tutors while also teaching you how to properly learn!
            </Typography>
            <Button
              sx={{
                bgcolor: '#018EA2',
                color: 'white',
                width: '200px',
                marginTop: '20px',
                '&:hover': { bgcolor: '#018EA2', color: 'white' },
              }}
            >
              Yes, I want better learning!
            </Button>
          </Stack>
          <img src={image} style={{ objectFit: 'fill', width: '350px', height: '350px' }} />
        </Stack>
      </div>
    </Carousel>
  );
}
