import React from 'react'
import image from '../Media/portrait-gorgeous-hispanic-preschool-teacher-teaching-her-students-classroom.png';
import { Stack, Typography } from '@mui/material';

export default function Sliders() {
    const text = "Make the best out of education through our Learning Approaches!";
const words = text.split(" ");
const groupedWords = [];
for (let i = 0; i < words.length; i += 3) {
  groupedWords.push(words.slice(i, i + 3).join(" "));
}
const formattedText = groupedWords.join("<br/>");

console.log(formattedText);
  return (
    <div>
      <Stack direction='row' gap={5}
       sx={{display:'flex',flexWrap:'wrap',justifyContent:'center',alignItems:'center'}}>
        <Typography sx={{fontSize:'20px',fontWeight:'bold',color:'#333134'}}>
        Make the best <br/>
out of education<br/>
through our Learning<br/>
Approaches!
        </Typography>
        <img src={image} style={{objectFit:'fill',width:'300px',height:'300px'}}/>
      </Stack>
    </div>
  )
}
