import React from 'react';
import image from '../Media/portrait-gorgeous-hispanic-preschool-teacher-teaching-her-students-classroom.png';
import { Stack, Typography } from '@mui/material';

export default function Sliders() {
  const lines = [
    "Make the best",
    "out of education",
    "through our Learning",
    "Approaches!"
  ];

  return (
    <div>
      <Stack direction="row" gap={5} sx={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', alignItems: 'center' }}>
        {lines.map((line, index) => (
          <Typography key={index} sx={{ fontSize: '20px', fontWeight: 'bold', color: '#333134' }}>
            {line}
          </Typography>
        ))}
        <img src={image} style={{ objectFit: 'fill', width: '300px', height: '300px' }} />
      </Stack>
    </div>
  );
}
