import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import image from '../Media/portrait-gorgeous-hispanic-preschool-teacher-teaching-her-students-classroom.png';
import { Box, Button, Stack, Typography, useMediaQuery } from '@mui/material';

export default function Sliders() {
  const matches = useMediaQuery('(min-width:375px)');

  const items = [
    {
      image: image,
      title: 
         " Make the best out of education through our Learning Approaches!",
      description: "Whether you’re a student looking to get tutored, or a parent who’s looking to hone their kid’s skills in learning;you’re in the right place! We’ll provide you with the best tutors while also teaching you how to properly learn!"
    }
  ];

  return (
    <div dir="rtl"> {/* Add the 'dir' attribute here */}
      <Carousel
        showThumbs={false}
        showStatus={false}
        autoPlay={true}
        renderIndicator={(onClickHandler, isSelected, index, label) => (
          <button
            type="button"
            style={{
              border: 'none',
              width: '20px',
              height: '3px',
              background: isSelected ? '#018EA2' : '#018EA278',
              margin: '0 5px',
              cursor: 'pointer',
            }}
            onClick={onClickHandler}
          />
        )}
      >
        {items.map((item, index) => (
          <div key={index}>
            <Stack
              direction="row"
              gap={7}
              sx={{
                display: 'flex',
                flexWrap: 'wrap',
                justifyContent: 'center',
                alignItems: 'center',
                padding: '20px',
              }}
            >
              <Stack sx={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'start', alignItems: 'start' }}>
                <Typography
                  sx={{ fontSize: '40px', fontWeight: 'bold', color: '#333134', whiteSpace: 'pre-line' }}
                >
                  {item.title}
                </Typography>
                <Typography sx={{ color: '#333134', fontSize: '15px', whiteSpace: 'pre-line' }}>
                  {item.description}
                </Typography>
                <Button
                  sx={{
                    bgcolor: '#018EA2',
                    color: 'white',
                    width: '200px',
                    marginTop: '20px',
                    '&:hover': { bgcolor: '#018EA2', color: 'white' },
                  }}
                >
                  Yes, I want better learning!
                </Button>
              </Stack>
              <img src={item.image} style={{ objectFit: 'fill', width: matches ? '350px' : '300px', height: '350px' }} />
            </Stack>
          </div>
        ))}
      </Carousel>
    </div>
  );
}
