import React, { useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import { Stack, Typography, useMediaQuery } from '@mui/material';
import { useTranslation } from 'react-i18next';
import prev from '../Media/Layer 2.png';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Blogs({ News }) {
  const matches = useMediaQuery('(min-width:1000px)');
  const [activeStep, setActiveStep] = useState(0);
  const itemsPerRow = matches ? 3 : 1;
  const maxSteps = News.length;
  const { t } = useTranslation();
  const navigate = useNavigate();

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + itemsPerRow);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - itemsPerRow);
  };

  const handleBlog = (id) => {
    console.log(id);
    axios.get(`${process.env.REACT_APP_API_URL}blog-details/${id}`).then((res) => {
      navigate('/blogDetails', { state: { data: res.data.data } });
    });
  };

  const displayCardsInRows = (cards) => {
    const rows = [];
    const totalRows = Math.ceil(cards.length / itemsPerRow);

    for (let i = 0; i < totalRows; i++) {
      const cardsForRow = cards.slice(i * itemsPerRow, (i + 1) * itemsPerRow);

      const row = (
        <div
          key={i}
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'center',
            alignItems: 'center',
            gap: '50px',
            marginBottom: '30px',
          }}
        >
          {cardsForRow.map((c) => (
            <Card key={c.id} sx={{ width: '300px', borderRadius: '10px' }}>
              <CardContent
                sx={{
                  padding: matches ? '30px' : '30px',
                  display: 'flex',
                  flexWrap: 'wrap',
                  justifyContent: 'start',
                  alignItems: 'start',
                  position: 'relative',
                }}
              >
                <Typography variant="h6">{c.title}</Typography>
                <Typography variant="body2">{c.description}</Typography>
                <Button onClick={() => handleBlog(c.id)}>{t('Read More')}</Button>
              </CardContent>
            </Card>
          ))}
        </div>
      );

      rows.push(row);
    }

    return rows;
  };

  return (
    <div style={{ backgroundColor: '#F1F1F1', padding: '20px' }}>
      {/* Card rows */}
      {displayCardsInRows(News)}

      {/* Navigation buttons */}
      {matches ? (
        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'center',
            alignItems: 'center',
            position: 'relative',
          }}
        >
          <Button
            sx={{
              height: '30px',
              color: '#7D4896',
              position: 'absolute',
              bottom: '220px',
              left: '50px',
              cursor: 'pointer',
            }}
            onClick={handleNext}
            disabled={activeStep + itemsPerRow >= maxSteps}
          >
            <img src={prev} style={{ width: '30px', height: '30px', cursor: 'pointer' }} alt="Next" />
          </Button>
          <Button
            sx={{
              color: '#7D4896',
              height: '30px',
              position: 'absolute',
              bottom: '220px',
              right: '50px',
              cursor: 'pointer',
            }}
            onClick={handleBack}
            disabled={activeStep === 0}
          >
            <img
              src={prev}
              style={{ width: '30px', height: '30px', transform: 'scaleX(-1)', cursor: 'pointer' }}
              alt="Previous"
            />
          </Button>
        </div>
      ) : null}
    </div>
  );
}
