import React, { useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import { Stack, Typography, useMediaQuery } from '@mui/material';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Blogs({ News }) {
  const matches = useMediaQuery('(min-width:1000px)');
  const handleBlog = (id) => {
    console.log(id);
    axios.get(`${process.env.REACT_APP_API_URL}blog-details/${id}`).then((res) => {
      navigate('/blogDetails', { state: { data: res.data.data } });
    });
  };
  const { t } = useTranslation();
  const navigate = useNavigate();

  const displayCardsInRows = () => {
    const rows = [];
    const itemsPerRow = 3;

    for (let i = 0; i < News.length; i += itemsPerRow) {
      const rowCards = News.slice(i, 3);

      const row = (
        <div className="row" key={i}>
          {rowCards.map((c) => (
            <div className="col-md-4" key={c.id}>
              <Card sx={{ width: '300px', borderRadius: '10px', minHeight: '450px' }}>
                <CardContent
                  sx={{
                    padding: matches ? '30px' : '30px',
                    display: 'flex',
                    flexWrap: 'wrap',
                    justifyContent: 'start',
                    alignItems: 'start',
                    position: 'relative',
                  }}
                >
                  <Stack>
                    <img
                      src={c.image}
                      style={{
                        borderRadius: '10px',
                        width: '240px',
                        height: '200px',
                        objectFit: 'fill',
                      }}
                    />
                    <Typography
                      sx={{
                        color: '#018EA2',
                        fontSize: '13px',
                        fontWeight: 'bold',
                        marginTop: c.date ? '10px' : '25px',
                      }}
                    >
                      {c.date}
                    </Typography>
                    <Typography
                      sx={{
                        marginTop: '10px',
                        color: 'black',
                        fontSize: '16px',
                        padding: '5px',
                        minHeight: '100px',
                      }}
                    >
                      {c.title}
                    </Typography>
                    <Button
                      sx={{
                        bgcolor: '#7D4896',
                        color: 'white',
                        borderRadius: '5px',
                        '&:hover': {
                          bgcolor: '#7D4896',
                          color: 'white',
                        },
                        fontSize: '16px',
                        textTransform: 'none',
                      }}
                      onClick={() => {
                        handleBlog(c.id);
                      }}
                    >
                      {t('Read more')}
                    </Button>
                  </Stack>
                </CardContent>
              </Card>
            </div>
          ))}
        </div>
      );

      rows.push(row);
    }

    return rows;
  };

  return (
    <div>
      <div className="navbar navfar">
        <a href="#">
          <i className="fa fa-instagram bisc"></i>
          <span className="mini"></span>
        </a>
      </div>

      <div className="container page-top">
        <div className="container">
          <div id="carouselExample" className="carousel slide" data-ride="carousel" data-interval="9000">
            <div className="carousel-inner row w-100 mx-auto" role="listbox">
              {displayCardsInRows()}
            </div>
            <a className="carousel-control-prev" href="#carouselExample" role="button" data-slide="prev">
              <span className="carousel-control-prev-icon" aria-hidden="true"></span>
              <span className="sr-only">Previous</span>
            </a>
            <a className="carousel-control-next text-faded" href="#carouselExample" role="button" data-slide="next">
              <span className="carousel-control-next-icon" aria-hidden="true"></span>
              <span className="sr-only">Next</span>
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}
