import React, { useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import { autoPlay } from 'react-swipeable-views-utils';
import SwipeableViews from 'react-swipeable-views';
import Button from '@mui/material/Button';
import { Stack, Typography, useMediaQuery } from '@mui/material';
import { useTranslation } from 'react-i18next';
import prev from '../Media/Layer 2.png';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import SwipeableDrawer from '@mui/material/SwipeableDrawer';

export default function Blogs({News}) {
  const matches = useMediaQuery('(min-width:1000px)');
  const handleBlog = (id) => {
    console.log(id);
    axios.get(`${process.env.REACT_APP_API_URL}blog-details/${id}`).then((res) => {
      navigate('/blogDetails', { state: { data: res.data.data } });
    });
  };
  const { t } = useTranslation();
  const navigate = useNavigate();

  return (
    <div>
      <div class="navbar navfar">
       <a href="#"><i class="fa fa-instagram bisc"></i><span class="mini"></span></a>
</div>

    <div class="container page-top">

        <div class="container">
            <div id="carouselExample" class="carousel slide" data-ride="carousel" data-interval="9000">
                <div class="carousel-inner row w-100 mx-auto" role="listbox">
                    <div class="carousel-item col-md-3  active">
                       {News.map((c)=>
                       <Card key={c.id} sx={{ width: '300px', borderRadius: '10px', minHeight: '450px' }}>
                                   <CardContent
                                     sx={{
                                       padding: matches ? '30px' : '30px',
                                       display: 'flex',
                                       flexWrap: 'wrap',
                                       justifyContent: 'start',
                                       alignItems: 'start',
                                       position: 'relative',
                                     }}
                                   >
                                       <Stack>
                                         <img
                                           src={c.image}
                                           style={{
                                             borderRadius: '10px',
                                             width: '240px',
                                             height: '200px',
                                             objectFit: 'fill',
                                           }}
                                         />
                                         {/* <div
                                           style={{
                                             position: 'absolute',
                                             bottom: '150px',
                                             width: '230px',
                                             left: matches ? '10%' : '10%',
                                             height: '40px',
                                              backgroundImage:
                                                'linear-gradient(to top, rgba(0, 0, 0, 0.8), rgba(255, 255, 255, 0))',
                                              padding: '5px',
                                              borderRadius: '10px',
                                            }}
                                          ></div> */}
                                       
                                          <Typography
                                            sx={{
                                              color: '#018EA2',
                                              fontSize: '13px',
                                              fontWeight: 'bold',
                                              marginTop: c.date ? '10px' : '25px',
                                            }}
                                          >
                                            {c.date}
                                          </Typography>
                                          <Typography
                                            sx={{
                                              marginTop:'10px',
                                              color: 'black',
                                              fontSize: '16px',
                                              padding: '5px',
                                              minHeight:'100px'
                                            }}
                                          >
                                            {c.title}
                                          </Typography>
                                          <Button
                                            sx={{
                                              bgcolor: '#7D4896',
                                              color: 'white',
                                              borderRadius: '5px',
                                              '&:hover': {
                                                bgcolor: '#7D4896',
                                                color: 'white',
                                              },
                                              fontSize: '16px',
                                              textTransform: 'none',
                                            }}
                                            onClick={() => {
                                             handleBlog(c.id);
                                           }}
                                         >
                                           {t('Read more')}
                                         </Button>
                                       </Stack>
                                       </CardContent>
                               </Card>)}
                    </div>
                    </div>
                </div>
                <a class="carousel-control-prev" href="#carouselExample" role="button" data-slide="prev">
                    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span class="sr-only">Previous</span>
                </a>
                <a class="carousel-control-next text-faded" href="#carouselExample" role="button" data-slide="next">
                    <span class="carousel-control-next-icon" aria-hidden="true"></span>
                    <span class="sr-only">Next</span>
                </a>
            </div>
        </div>
    </div>
  )
}
