import { Box, Accordion, AccordionSummary,useMediaQuery, AccordionDetails, Typography, Container } from '@mui/material';
import React, { useState } from 'react';
import { useTranslation } from "react-i18next";
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import { autoPlay } from 'react-swipeable-views-utils';
import SwipeableViews from 'react-swipeable-views';
import Button from '@mui/material/Button';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);
export default function FAQs({questions,setQuestions}) {
  const { t } = useTranslation();
  const [activeStep, setActiveStep] = useState(0);
  const itemsPerColumn = 3 ;
  const maxSteps = questions.length;
  const matches = useMediaQuery('(min-width:645px)');
  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleStepChange = (step) => {
    setActiveStep(step);
  };

  const toggleQuestion = (index) => {
    const newQuestions = [...questions];
    newQuestions[index].isOpen = !newQuestions[index].isOpen;
    setQuestions(newQuestions);
  };

    

  return (
    <div id={t('FAQs')} sx={{ marginBottom: '30px' }}>
      <Box sx={{
        backgroundColor: '#F5C660', width: '100%', justifyContent: 'center',
        alignItems: 'center', paddingBottom: '60px'
      }}>
        <Typography sx={{
          display: 'flex', flexWrap: 'wrap', justifyContent: 'center', alignItems: 'center',
          fontWeight: 'bold', fontSize: '30px', color: '#7D4896', marginBottom: '0px',paddingTop:'40px'
        }}>
          {t('FAQs')}
        </Typography>
        <AutoPlaySwipeableViews
        index={activeStep}
        onChangeIndex={handleStepChange}
        interval={5000}
      >
        {displayCardsInRows(questions)}
      </AutoPlaySwipeableViews>

     <div style={{ display:'flex',flexWrap:'wrap',justifyContent:'center',alignItems:'center',position:'relative' }}>
      <Button sx={{ height: '30px', color: '#7D4896',position:'absolute',
      bottom:'20px',left:matches?'50px':'0px',cursor:'pointer'}}
              onClick={handleNext}
              disabled={activeStep === questions.length -1  }
          >
            <ArrowBackIosNewIcon />
          </Button>
          <Button
              sx={{ color: '#7D4896', height: '30px', position:'absolute',bottom:'20px',right:matches?'50px':'0px'
              ,cursor:'pointer' }}
               onClick={handleBack} disabled={activeStep === 0}>
              <ArrowForwardIosIcon />
              </Button></div> :<></>
      </Box>
    </div>
  );
}
