import { TextField, Grid, Stack, useMediaQuery, Button, MenuItem } from '@mui/material';
import React from 'react';
import { useTranslation } from 'react-i18next';

export default function Preferences({selectedarea,setSelectedArea,area}) {
    const { t } = useTranslation();

  return (
    <div style={{marginTop:'30px'}}>
       <Grid sx={{display:'flex',flexDirection:'column'}}>
         <label style={{color:'#018EA2' , marginBottom:'10px'}}>
            {t("Preferred Areas")}
            </label>
            <TextField
            select
            SelectProps={{ value: selectedarea, multiple: true }}
              value={selectedarea}
              onChange={(e) => { setSelectedArea(e.target.value)}}
              InputLabelProps={{
                sx: {
                  color: '#7D4896'
                }
              }}
              sx={{width: '100%',
              '.MuiInputBase-root':{color:"black",height:'40px',border: '1.2px solid #7D4896'} 
            }}
            >
                {area.map((c) => (
                <MenuItem value={c.id}>
                  {c.name}
                </MenuItem>
              ))}
              </TextField>
          </Grid>
          
          <label style={{color:'#018EA2' , marginBottom:'10px'}}>
            {t("Availability")}
            </label>
    </div>
  )
}
