import React, { useState } from 'react';
import { Button, Stack, Typography, useMediaQuery } from '@mui/material';
import { autoPlay } from 'react-swipeable-views-utils';
import SwipeableViews from 'react-swipeable-views';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

export default function Sliders({ slider }) {
  const matches = useMediaQuery('(min-width:375px)');
  const lang = localStorage.getItem('lang');
  const [selectedSlideIndex, setSelectedSlideIndex] = useState(0);

  return (
    <div>
      <AutoPlaySwipeableViews
        interval={3000}
        enableMouseEvents
        onChangeIndex={(index) => setSelectedSlideIndex(index)}
       
      >
        {slider.map((slider, index) => (
          <div key={index}>
            <Stack
              direction="row"
              gap={20}
              sx={{
                display: 'flex',
                flexWrap: 'wrap',
                justifyContent: 'center',
                alignItems: 'center',
                padding: '20px',
              }}
            >
              <Stack sx={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'start', alignItems: 'start' }}>
                <Typography
                  sx={{
                    fontSize: '40px',
                    fontWeight: 'bold',
                    color: '#333134',
                    whiteSpace: 'pre-line',
                  }}
                >
                  {slider.title.split('\n').map((line, lineIndex) => (
                    <React.Fragment key={lineIndex}>
                      {line}
                      <br />
                    </React.Fragment>
                  ))}
                </Typography>
                <Typography sx={{ color: '#333134', fontSize: '15px', whiteSpace: 'pre-line' }}>
                  {slider.description.split('\n').map((line, lineIndex) => (
                    <React.Fragment key={lineIndex}>
                      {line}
                      <br />
                    </React.Fragment>
                  ))}
                </Typography>
                <Button
                  sx={{
                    bgcolor: '#018EA2',
                    color: 'white',
                    width: '200px',
                    marginTop: '20px',
                    '&:hover': { bgcolor: '#018EA2', color: 'white' },
                  }}
                  href={slider.action}
                >
                  {slider.button_text}
                </Button>
              </Stack>
              <img
                src={slider.image}
                style={{ objectFit: 'fill', width: matches ? '350px' : '300px', height: '350px', borderRadius: '20px' }}
                alt="Slider Image"
              />
            </Stack>
          </div>
        ))}
      </AutoPlaySwipeableViews>
    </div>
  );
}
