import React, { useEffect, useRef } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import { Navigation, Pagination, Scrollbar, A11y } from 'swiper/modules';
import 'swiper/less/navigation';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import { Stack, Typography, useMediaQuery } from '@mui/material';
import { useTranslation } from 'react-i18next';
import prev from '../Media/Layer 2.png';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import SwipeableDrawer from '@mui/material/SwipeableDrawer';

export default function Blogs({News}) {
 
  return (
   
    
    <Swiper
    modules={[Navigation]}
    spaceBetween={50}
    slidesPerView={3}
    navigation
  >
     <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'center',
            alignItems: 'center',
            gap: '50px',
            marginBottom: '30px',
          }}
        >
          {News.map((c) => (
            <Card key={c.id} sx={{ width: '300px', borderRadius: '10px', minHeight: '450px' }}>
              <CardContent
                sx={{
                  padding: matches ? '30px' : '30px',
                  display: 'flex',
                  flexWrap: 'wrap',
                  justifyContent: 'start',
                  alignItems: 'start',
                  position: 'relative',
                }}
              >
                <Stack>
                  <img
                    src={c.image}
                    style={{
                      borderRadius: '10px',
                      width: '240px',
                      height: '200px',
                      objectFit: 'fill',
                    }}
                  />
                  {/* <div
                    style={{
                      position: 'absolute',
                      bottom: '150px',
                      width: '230px',
                      left: matches ? '10%' : '10%',
                      height: '40px',
                      backgroundImage:
                        'linear-gradient(to top, rgba(0, 0, 0, 0.8), rgba(255, 255, 255, 0))',
                      padding: '5px',
                      borderRadius: '10px',
                    }}
                  ></div> */}
                
                  <Typography
                    sx={{
                      color: '#018EA2',
                      fontSize: '13px',
                      fontWeight: 'bold',
                      marginTop: c.date ? '10px' : '25px',
                    }}
                  >
                    {c.date}
                  </Typography>
                  <Typography
                    sx={{
                      marginTop:'10px',
                      color: 'black',
                      fontSize: '16px',
                      padding: '5px',
                      minHeight:'100px'
                    }}
                  >
                    {c.title}
                  </Typography>
                  <Button
                    sx={{
                      bgcolor: '#7D4896',
                      color: 'white',
                      borderRadius: '5px',
                      '&:hover': {
                        bgcolor: '#7D4896',
                        color: 'white',
                      },
                      fontSize: '16px',
                      textTransform: 'none',
                    }}
                    onClick={() => {
                      handleBlog(c.id);
                    }}
                  >
                    {t('Read more')}
                  </Button>
                </Stack>
              </CardContent>
            </Card>
          ))}
        </div>
    </Swiper>
    
  );
}
