import { TextField, Grid, Stack, useMediaQuery, Button, MenuItem } from '@mui/material';
import React from 'react';
import { useTranslation } from 'react-i18next';

export default function Preferences({selectedarea,setSelectedArea,area}) {
  return (
    <div style={{marginTop:'30px'}}>
       <Grid sx={{display:'flex',flexDirection:'column'}}>
         <label style={{color:'#707070' , marginBottom:'10px'}}>
            {t("Preferred Areas")}
            </label>
            <TextField
            select
            SelectProps={{ value: selectedarea, multiple: true }}
              value={selectedprogram}
              onChange={(e) => { setSelectedStage(e.target.value) ;setAlerting(false)}}
              InputLabelProps={{
                sx: {
                  color: '#7D4896'
                }
              }}
              sx={{width: '400px',
              '.MuiInputBase-root':{color:"black",height:'40px',border: '1.2px solid #7D4896'} 
            }}
            >
                {stage.map((c) => (
                <MenuItem value={c.id}>
                  {c.name}
                </MenuItem>
              ))}
              </TextField>
          </Grid>
    </div>
  )
}
