import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation} from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import { Stack, Typography, useMediaQuery } from '@mui/material';
import { useTranslation } from 'react-i18next';
import prev from '../Media/Layer 2.png';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useSwiper } from 'swiper/react';
import ''
export default function Blogs({ News }) {
  const swiper = useSwiper();
  const { t } = useTranslation();
  const matches = useMediaQuery('(min-width:1000px)');
  const navigate = useNavigate();
  const handleBlog = (id) => {
    console.log(id);
    axios.get(`${process.env.REACT_APP_API_URL}blog-details/${id}`).then((res) => {
      navigate('/blogDetails', { state: { data: res.data.data } });
    });
  };

  return (
    <div style={{ backgroundColor: '#F1F1F1', padding: '100px' }}>
      <Typography
        sx={{
          fontWeight: 'bold',
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',
          alignItems: 'center',
          fontWeight: 'bold',
          fontSize: '30px',
          marginTop: '-30px',
          color: '#7D4896',
          marginBottom: '70px',
        }}
      >
        {t('The ATL Blog')}
      </Typography>

      <Swiper
        modules={[Navigation]}
        spaceBetween={50}
        slidesPerView={3}
        navigation={{
          prevEl: '.swiper-button-prev',
          nextEl: '.swiper-button-next',
        }}
        onSwiper={(swiper) => console.log(swiper)}
        onSlideChange={() => console.log('slide change')}
      >
        <div>
          {News.map((c) => (
            <SwiperSlide style={{ backgroundColor: '#F1F1F1' }}>
              <Card
                key={c.id}
                sx={{ width: '300px', borderRadius: '10px', minHeight: '450px', marginBottom: '20px' }}
              >
                <CardContent
                  sx={{
                    padding: matches ? '30px' : '30px',
                    display: 'flex',
                    flexWrap: 'wrap',
                    justifyContent: 'start',
                    alignItems: 'start',
                    position: 'relative',
                  }}
                >
                  <Stack>
                    <img
                      src={c.image}
                      style={{
                        borderRadius: '10px',
                        width: '240px',
                        height: '200px',
                        objectFit: 'fill',
                      }}
                    />
                    <Typography
                      sx={{
                        color: '#018EA2',
                        fontSize: '13px',
                        fontWeight: 'bold',
                        marginTop: c.date ? '10px' : '25px',
                      }}
                    >
                      {c.date}
                    </Typography>
                    <Typography
                      sx={{
                        marginTop: '10px',
                        color: 'black',
                        fontSize: '16px',
                        padding: '5px',
                        minHeight: '100px',
                      }}
                    >
                      {c.title}
                    </Typography>
                    <Button
                      sx={{
                        bgcolor: '#7D4896',
                        color: 'white',
                        borderRadius: '5px',
                        '&:hover': {
                          bgcolor: '#7D4896',
                          color: 'white',
                        },
                        fontSize: '16px',
                        textTransform: 'none',
                      }}
                      onClick={() => {
                        handleBlog(c.id);
                      }}
                    >
                      {t('Read more')}
                    </Button>
                  </Stack>
                </CardContent>
              </Card>
            </SwiperSlide>
          ))}
        </div>

        <div className="swiper-button-prev" 
        style={{ backgroundImage: `url(${prev})`,backgroundSize:'contain',
        backgroundRepeat:'no-repeat',content:'' }}>
        </div>
        <div className="swiper-button-next" style={{  backgroundImage: `url(${prev})`,backgroundSize:'contain',backgroundRepeat:'no-repeat',
      transform:'scaleX(-1)'}}></div>
      </Swiper>
    </div>
  );
}
