import React from 'react'
import pic from '../Media/Rectangle 898.png'
import { useLocation } from 'react-router-dom';
import { Container, Stack, Typography } from '@mui/material';
export default function BlogDetails() {
    const location = useLocation()
    const { state } = location;
    const { data } = state; 

  return (
    <div style={{marginTop:'90px'}}>
        <div style={{backgroundColor:'#7D4896',height:'350px'}}></div>
        <div style={{display:'flex',flexWrap:'wrap',justifyContent:'center',alignItems:'center',padding:'30px'}}>
        <Typography sx={{fontSize:'20px',color:'white'}}>
            {data.blog_details.title}
        </Typography>
        <Typography sx={{fontSize:'20px',color:'white'}}>
            {data.blog_details.title}
        </Typography>
        </div>
      <img src={data.blog_details.image} style={{height:'350px',width:'70%',top:'30%',position:'absolute',
      marginLeft:'16%',objectFit:'fill'}}/>
      <Container maxWidth='md' sx={{display:'flex',flexWrap:'wrap',justifyContent:'start',alignItems:'start'}}>
        <Stack>
      <Typography sx={{marginBottom:'30px',color:'#888888',marginTop:'40px'}}>
        {data.blog_details.description}
      </Typography>

      <Typography sx={{marginBottom:'20px',color:'#7D4896',fontWeight:'bold'}}>
      More Articles
      </Typography>

      <Stack direction='row' gap={2} sx={{display:'flex',flexWrap:'wrap',justifyContent:'start',alignItems:'start',marginBottom:'50px'}}>
        {data.blogs.map((b)=>
        <div style={{position:'relative'}}>
        <img style={{width:'200px',height:'200px',borderRadius:'20px'}} src={b.image} />
        
        </div>
        )}
      </Stack>
      </Stack>
      </Container>

    </div>
  )
}
