import { Box, Typography } from '@mui/material';
import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { virtualize } from 'react-swipeable-views-utils';
import SwipeableViews from 'react-swipeable-views';
import 'react-swipeable-views/lib/styles.css';

import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';

const VirtualizedSwipeableViews = virtualize(SwipeableViews);

export default function FAQs() {
  const { t } = useTranslation();
  const [questions, setQuestions] = useState([
    {
      Question: 'What are the cars specifications',
      answer:
        '1)-Classic cars are cars for daily use and are considered the most widely used category in cities   2)-VIP cars are modern cars for use within cities or between governorates. They are comfortable, air-conditioned cars suitable for businessmen and special occasions 3)-VAN cars are a modern, air-conditioned car with a capacity of up to 11 passengers, suitable for companies, institutions, special events, and tourist trips 4)-Mini-VAN cars with a capacity of 7 passengers are used within cities and for transporting employees. It is considered the most economical category if the number of passengers is (5-7) passengers.',
    },
    {
      Question: 'What types of cars are available',
      answer: 'Classic – VIP – VAN – mini-VAN',
    },
    // Add more questions here
  ]);

  const toggleQuestion = (index) => {
    const newQuestions = [...questions];
    newQuestions[index].isOpen = !newQuestions[index].isOpen;
    setQuestions(newQuestions);
  };

  const handleChangeIndex = (index) => {
    // Handle the index change if needed
  };

  return (
    <div style={{ marginBottom: '30px' }}>
      <Box
        style={{
          backgroundColor: '#F5C660',
          width: '100%',
          justifyContent: 'center',
          alignItems: 'center',
          paddingBottom: '60px',
        }}
      >
        <Typography
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'center',
            alignItems: 'center',
            fontWeight: 'bold',
            fontSize: '30px',
            color: '#7D4896',
            marginBottom: '20px',
            paddingTop: '40px',
          }}
        >
          {t('FAQs')}
        </Typography>

        <VirtualizedSwipeableViews
          axis="x"
          index={0}
          onChangeIndex={handleChangeIndex}
          slideRenderer={({ key, index }) => (
            <div key={key}>
              {index < questions.length && (
                <Accordion
                  style={{
                    borderBottom: '2px solid #7D4896',
                    width: '60%',
                    marginLeft: '15%',
                    backgroundColor: 'transparent',
                    '&:before': { display: 'none' },
                    padding: '30px',
                    '&:last-child': {
                      borderBottom: '2px solid #7D4896',
                      width: '60%',
                      marginLeft: '15%',
                    },
                  }}
                  elevation={0}
                  disableGutters
                  expanded={questions[index].isOpen}
                  onChange={() => toggleQuestion(index)}
                >
                  <AccordionSummary>
                    {questions[index].isOpen ? (
                      <RemoveIcon
                        style={{ color: '#7D4896', marginRight: '20px', marginTop: '5px' }}
                      />
                    ) : (
                      <AddIcon
                        style={{ color: '#7D4896', marginRight: '20px', marginTop: '5px' }}
                      />
                    )}
                    <Typography
                      style={{ color: '#7D4896', fontSize: '20px', fontWeight: 'bold' }}
                    >
                      {questions[index].Question}
                    </Typography>
                  </AccordionSummary>
                  <AccordionDetails>
                    <Typography style={{ color: '#7D4896', fontSize: '16px' }}>
                      {questions[index].answer}
                    </Typography>
                  </AccordionDetails>
                </Accordion>
              )}
            </div>
          )}
        />
      </Box>
    </div>
  );
}
