import pic from '../Media/young-tender-curly-girl-holding-documents.png'
import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import Button from '@mui/material/Button';
import styled from '@emotion/styled';
import axios from 'axios';
import { Alert, Container, Stack, StepButton, Typography } from '@mui/material';
import PersonalInfo from '../Component/PersonalInfo';

const steps = ['Personal Information', 'Career Information', 'Preferences'];

export default function FormPage() {
    const [name, setName] = useState('');
    const [lastName, setLastName] = useState('');
    const [phone, setPhone] = useState('');
    const [birthday, setBirthday] = useState('');
    const [email, setEmail] = useState('');
    const [selectedGender, setselectedGender] = useState('');
    const [gender,setgender] = useState(['female','male'])
    const [activeStep, setActiveStep] = React.useState(0);
    const [skipped, setSkipped] = React.useState(new Set());
    const [completed, setCompleted] = React.useState({});
    const [alerting, setAlerting] = React.useState(false);
    const [checked, setChecked] = useState(false);
    
    useEffect(() => {
        // Load form data from local storage when the component mounts
        const formData = JSON.parse(localStorage.getItem('formData'));
        if (formData) {
          setName(formData.name);
          setLastName(formData.lastName);
          setPhone(formData.phone);
          setBirthday(formData.birthday);
          setEmail(formData.email);
          setselectedGender(formData.selectedGender);
          setChecked(formData.checked);
        }
      }, []);
    
      useEffect(() => {
        const formData = {
          name,
          lastName,
          phone,
          birthday,
          email,
          selectedGender,
          checked,
        };
        localStorage.setItem('formData', JSON.stringify(formData));
      }, [name, lastName, phone, birthday, email, selectedGender, checked]);

    const StyledStepIcon = styled.div`
      width: 30px;
      height: 30px;
      border-radius: 50%;
      color: white;
      background-color: ${props => props.active ? '#7D4896' : '#ccc'};
      display: flex;
      flex-wrap:wrap;
      align-items: center;
      justify-content: center;
    `;
  
    const isStepSkipped = (step) => {
      return skipped.has(step);
    };

    const handleNext = () => {
      if(activeStep === 0 ){
        if(name && lastName && selectedGender && phone && birthday && email && checked){
        let newSkipped = skipped;
        if (isStepSkipped(activeStep)) {
          newSkipped = new Set(newSkipped.values());
          newSkipped.delete(activeStep);
        }
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
        setSkipped(newSkipped);
        setAlerting(true)
      }}
      if(!name || !lastName || !selectedGender || !phone || !birthday || !email || !checked){
        setAlerting(true)
      }
    };
  
    const handleFinish =() =>{
    //   const formData = new FormData();
    //   formData.append('firstname', name);
    //   formData.append('lastname', lastName);
    //   formData.append('age', age);
    //   formData.append('gender', gender);
    //   formData.append('email', recoveryEmail);
    //   formData.append('phone', phone);
    //   formData.append('password', password);
    //   formData.append('avatar', name);
    //   formData.append('nationality', city_id);
    //   formData.append('interests', interest);
    //   for (var pair of formData.entries()) {
    //     console.log(pair[0]+ ', ' + pair[1]); 
    // }
    // axios.post(`${process.env.REACT_APP_API_URL}register`,formData).then(res=>{
    //   console.log(res.data)
    // })
    }

  return (
    <div style={{marginTop:'90px'}}>
      <img src={pic} style={{width:'100%',height:'500px',objectFit:'cover'}}/>
      <div>
        <Container maxWidth='md' >
            <Stack sx={{ alignItems: 'center', justifyContent: 'center',display:'flex',flexWrap:'wrap',justifyContent:'center',alignItems:'center'}}>
        <Typography sx={{fontSize:'30px',color:'black',marginTop:'50px'}}>
        Become a Tutor
        </Typography>
        <Typography sx={{fontSize:'16px',color:'black',marginTop:'15px'}}>
        Kindly fill the following form so we can get an idea of your experience, qualifications, 
        </Typography>

        <Typography sx={{fontSize:'16px',color:'black',marginTop:'5px'}}>
        and better sort you into the best fit for a greater teaching experience!
        </Typography>
        </Stack>
        </Container>
      </div>
      <div style={{ width: '100%',
     alignItems: 'start', justifyContent: 'center', 
     display: 'flex',marginTop:'30px'}}>
      <Box sx={{ width: '70%' ,padding:"40px"}}>
      <Stepper nonLinear activeStep={activeStep}>
        {steps.map((label, index) => {
        const labelProps = {};
        return (
          <Step key={label} completed={completed[index]} >
         <StepLabel
  {...labelProps}
  StepIconComponent={(stepIconProps) => (
    <StyledStepIcon active={stepIconProps.active}>
      {stepIconProps.icon}
    </StyledStepIcon>
  )}
>
  {label}
</StepLabel>
          </Step>)
        })}
      </Stepper>
        {activeStep === steps.length ? (
          <div>hello</div>
        ) : (
          <React.Fragment>
              {activeStep === 0 && (
                <div style={{ padding:'20px' }}>
               <PersonalInfo name={name} setName={setName} gender={gender} selectedGender={selectedGender} setselectedGender={setselectedGender}
               phone={phone} setPhone={setPhone} birthday={birthday} setBirthday={setBirthday} email={email}
               setEmail={setEmail} lastName={lastName} setLastName={setLastName}
               checked={checked} setChecked={setChecked} setAlerting={setAlerting} />
                </div>
            )}
              {activeStep === 1 && (
                <div>
                hi
                </div>
            )}
               {activeStep === 2 && (
                'hhh'
            )}
            <Box sx={{ display: 'flex', flexDirection: 'column' }}>
                {alerting && <Alert severity="error" sx={{width:'96%'}}>All feilds must be fiiled</Alert>}
             {activeStep !== steps.length - 1 ? 
             <Button onClick={handleNext} sx={{ color:'white',bgcolor:'#7D4896',width:'100%',fontSize:'16px',fontWeight:'bold',
             '&:hover':{color:'white',bgcolor:'#7D4896'},marginTop:'20px' }}>
                Next
              </Button> 
             : 

              <Button onClick={handleFinish} sx={{ color:'white',bgcolor:'#7D4896',width:'100%',fontSize:'16px',fontWeight:'bold',
              '&:hover':{color:'white',bgcolor:'#7D4896'},marginTop:'20px' }}>
                Submit
              </Button>}
            </Box>
          </React.Fragment>
        )}
      </Box>
    </div>
    </div>
  )
}
