import React from 'react'
import pic from '../Media/Group 9671.png'
import { Stack, Typography } from '@mui/material'
import pic2 from '../Media/Group 9660.png';

export default function WhyUs() {
    const item =[{
        number:1,
        title:'Simplified Coordination:',
        discription:"Parents will appreciate and enjoy the convenience of a single point of contact for arranging tutoring sessions across various subjects, saving time and effort."
},
{
    number:2,
    title:'Simplified Coordination:',
    discription:"Parents will appreciate and enjoy the convenience of a single point of contact for arranging tutoring sessions across various subjects, saving time and effort."
},
{
    number:3,
    title:'Simplified Coordination:',
    discription:"Parents will appreciate and enjoy the convenience of a single point of contact for arranging tutoring sessions across various subjects, saving time and effort."
}]
  return (
    <div style={{backgroundColor:'#7D4896',position:'relative',minHeight:'550px'}}>
      <Stack sx={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'start',
       alignItems: 'start',padding:'50px' }}>
        <Typography sx={{color:'#F5C660',fontSize:'40px',fontWeight:'bold'}}>
        What makes <br/>
        ATL Different? 
        </Typography>
        <img src={pic} style={{height:'450px',width:'300px',position:'absolute',right:'0px',top:'10%'}}/>

        {item.map((item,i)=>
        <>
            <img src={pic2} style={{width:'50px',height:'50px',objectFit:'fill',position:'relative'}}/>
        <Typography sx={{color:'white',position:'absolute',top:'5px'}}>
        {item.number}
        </Typography>
        </>
)}
      </Stack>
    </div>
  )
}
