import React, { useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import { autoPlay } from 'react-swipeable-views-utils';
import SwipeableViews from 'react-swipeable-views';
import Button from '@mui/material/Button';
import { Stack, Typography, useMediaQuery } from '@mui/material';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

export default function Blogs({ News }) {
  const matches = useMediaQuery('(min-width:1000px)');
  const [activeStep, setActiveStep] = useState(0);
  const itemsPerPage = matches ? 3 : 1;
  const maxSteps = Math.ceil(News.length / itemsPerPage);
  const { t } = useTranslation();
  const navigate = useNavigate();

  const handleNext = () => {
    setActiveStep((prevActiveStep) => (prevActiveStep + 1) % maxSteps);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => (prevActiveStep - 1 + maxSteps) % maxSteps);
  };

  const handleStepChange = (step) => {
    setActiveStep(step);
  };

  const handleBlog = (id) => {
    console.log(id);
    axios.get(`${process.env.REACT_APP_API_URL}blog-details/${id}`).then((res) => {
      navigate('/blogDetails', { state: { data: res.data.data } });
    });
  };

  const displayCardsInRow = () => {
    const startIndex = activeStep * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;

    const rowCards = News.slice(startIndex, endIndex);

    return (
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',
          alignItems: 'center',
          gap: '50px',
          marginBottom: '30px',
        }}
      >
        {rowCards.map((c) => (
          <Card key={c.id} sx={{ width: '300px', borderRadius: '10px', minHeight: '450px' }}>
            <CardContent
              sx={{
                padding: matches ? '30px' : '30px',
                display: 'flex',
                flexWrap: 'wrap',
                justifyContent: 'start',
                alignItems: 'start',
                position: 'relative',
              }}
            >
              <Stack>
                <img
                  src={c.image}
                  style={{
                    borderRadius: '10px',
                    width: '240px',
                    height: '200px',
                    objectFit: 'fill',
                  }}
                />
                <Typography
                  sx={{
                    color: '#018EA2',
                    fontSize: '13px',
                    fontWeight: 'bold',
                    marginTop: c.date ? '10px' : '25px',
                  }}
                >
                  {c.date}
                </Typography>
                <Typography
                  sx={{
                    marginTop: '10px',
                    color: 'black',
                    fontSize: '16px',
                    padding: '5px',
                    minHeight: '100px',
                  }}
                >
                  {c.title}
                </Typography>
                <Button
                  sx={{
                    bgcolor: '#7D4896',
                    color: 'white',
                    borderRadius: '5px',
                    '&:hover': {
                      bgcolor: '#7D4896',
                      color: 'white',
                    },
                    fontSize: '16px',
                    textTransform: 'none',
                  }}
                  onClick={() => {
                    handleBlog(c.id);
                  }}
                >
                  {t('Read more')}
                </Button>
              </Stack>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  };

  return (
    <div style={{ backgroundColor: '#F1F1F1', padding: '20px' }}>
      <Typography
        sx={{
          fontWeight: 'bold',
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',
          alignItems: 'center',
          fontWeight: 'bold',
          fontSize: '30px',
          marginTop: '20px',
          color: '#7D4896',
          marginBottom: '20px',
        }}
      >
        {t('The ATL Blog')}
      </Typography>
      <AutoPlaySwipeableViews
        axis="x"
        index={activeStep}
        onChangeIndex={handleStepChange}
        enableMouseEvents
        interval={5000}
      >
        {Array.from(Array(maxSteps).keys()).map((step) => (
          <div key={step}>
            {displayCardsInRow()}
          </div>
        ))}
      </AutoPlaySwipeableViews>
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <Button
          disabled={activeStep === 0}
          onClick={handleBack}
          sx={{
            bgcolor: '#7D4896',
            color: 'white',
            borderRadius: '5px',
            '&:hover': {
              bgcolor: '#7D4896',
              color: 'white',
            },
            fontSize: '16px',
            textTransform: 'none',
            marginRight: '10px',
          }}
        >
          {t('Previous')}
        </Button>
        <Button
          disabled={activeStep === maxSteps - 1}
          onClick={handleNext}
          sx={{
            bgcolor: '#7D4896',
            color: 'white',
            borderRadius: '5px',
            '&:hover': {
              bgcolor: '#7D4896',
              color: 'white',
            },
            fontSize: '16px',
            textTransform: 'none',
          }}
        >
          {t('Next')}
        </Button>
      </div>
    </div>
  );
}
