import React from 'react';
import { autoPlay } from 'react-swipeable-views-utils';
import { virtualize, bindKeyboard } from 'react-swipeable-views-utils';
import { Slider, Slide } from 'react-swipeable-views';
import 'react-swipeable-views/lib/styles.css';
import image from '../Media/portrait-gorgeous-hispanic-preschool-teacher-teaching-her-students-classroom.png';
import { Button, Stack, Typography, useMediaQuery } from '@mui/material';

const AutoPlaySlider = autoPlay(Slider);
const VirtualizeSlider = virtualize(AutoPlaySlider);

export default function Sliders() {
  const matches = useMediaQuery('(min-width: 375px)');
  const lang = localStorage.getItem('lang');
  const items = [
    {
      image: image,
      title: "Make the best out of education\nthrough our Learning Approaches!",
      description:
        "Whether you’re a student looking to get tutored, or a parent who’s looking to hone their kid’s skills in learning;\nyou’re in the right place! We’ll provide you with the best tutors while also teaching you how to properly learn!",
    },
    {
      image: image,
      title: "Another Slide",
      description: "This is another slide in the carousel. You can add as many slides as you want.",
    },
    {
      image: image,
      title: "Make the best out of education\nthrough our Learning Approaches!",
      description:
        "Whether you’re a student looking to get tutored, or a parent who’s looking to hone their kid’s skills in learning;\nyou’re in the right place! We’ll provide you with the best tutors while also teaching you how to properly learn!",
    },
  ];

  return (
    <VirtualizeSlider
      className="slider"
      interval={3000}
      enableMouseEvents
      {...bindKeyboard()}
    >
      {items.map((item, index) => (
        <Slide key={index}>
          <Stack
            direction="row"
            gap={7}
            sx={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', alignItems: 'center', padding: '20px' }}
          >
            <Stack sx={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'start', alignItems: 'start' }}>
              <Typography
                sx={{
                  fontSize: '40px',
                  fontWeight: 'bold',
                  color: '#333134',
                  whiteSpace: 'pre-line',
                }}
              >
                {item.title.split('\n').map((line, lineIndex) => (
                  <React.Fragment key={lineIndex}>
                    {line}
                    <br />
                  </React.Fragment>
                ))}
              </Typography>
              <Typography sx={{ color: '#333134', fontSize: '15px', whiteSpace: 'pre-line' }}>
                {item.description.split('\n').map((line, lineIndex) => (
                  <React.Fragment key={lineIndex}>
                    {line}
                    <br />
                  </React.Fragment>
                ))}
              </Typography>
              <Button
                sx={{
                  bgcolor: '#018EA2',
                  color: 'white',
                  width: '200px',
                  marginTop: '20px',
                  '&:hover': { bgcolor: '#018EA2', color: 'white' },
                }}
              >
                Yes, I want better learning!
              </Button>
            </Stack>
            <img
              src={item.image}
              style={{ objectFit: 'fill', width: matches ? '350px' : '300px', height: '350px' }}
              alt={`Slide ${index}`}
            />
          </Stack>
        </Slide>
      ))}
    </VirtualizeSlider>
  );
}
