import React from 'react'
import pic from '../Media/Rectangle 898.png'
import { useLocation } from 'react-router-dom';
import { Typography } from '@mui/material';
export default function BlogDetails() {
    const location = useLocation()
    const { state } = location;
    const { data } = state; 
  return (
    <div style={{marginTop:'90px',backgroundColor:'#7D4896',height:'300px',position:'relative'}}>
        <div style={{display:'flex',flexWrap:'wrap',justifyContent:'center',alignItems:'center',padding:'30px'}}>
        <Typography sx={{fontSize:'20px',color:'white'}}>
            {data.blog_details.title}
        </Typography>
        <Typography sx={{fontSize:'20px',color:'white'}}>
            {data.blog_details.title}
        </Typography>
        </div>
      <img src={data.blog_details.image} style={{height:'350px',width:'60%',top:'40%',marginLeft:'20%',objectFit:'fill'}}/>
      <Typography>
        {data.blog_details.description}
      </Typography>
    </div>
  )
}
