import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import MenuItem from '@mui/material/MenuItem';
import logo from './Media/Group 9656.png';
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import { useMediaQuery } from '@mui/material';
import LanguageIcon from '@mui/icons-material/Language';
import { useTranslation } from 'react-i18next';
import i18next from "i18next";

const pages = ['Home', 'What ATL?', 'Our Services', 'Become a Tutor', 'FAQs'];

function Navbar() {
  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [anchorElUser, setAnchorElUser] = React.useState(null);
  const matches = useMediaQuery('(min-width:939px)');
  const [langMenu,setLangMenu]=React.useState(null)
  const { t , i18n } = useTranslation();

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseNavMenu = (i) => {
    setAnchorElNav(null);
    const sectionElement = document.getElementById(i);
    sectionElement.scrollIntoView();
  };
  const handleClose = () => {
    setAnchorElNav(null);
  };
  const handleCloseLangMenu =() =>{
    setLangMenu(null)
  }
  const handleEn =() =>{
    i18next.changeLanguage('en')
    handleCloseLangMenu()
  }
  const handleAr =() =>{
    i18next.changeLanguage('ar')
    handleCloseLangMenu()
  }

  const handleQuote = () => {
    handleClose();
  };

  return (
    <>
      <AppBar sx={{ backgroundColor: 'white', padding: '10px' }}>
        <Container maxWidth="xl">
          <Toolbar>
            <Typography
              variant="h6"
              noWrap
              component="a"
              href="/"
              sx={{
                mr: 2,
                display: { xs: 'none', md: 'flex' },
                fontWeight: 700,
                letterSpacing: '.3rem',
                color: 'inherit',
                textDecoration: 'none',
              }}
            >
              <img src={logo} alt="...loading" style={{ width: '60px', height: '60px' }} />
            </Typography>

            <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' }, alignItems: 'center' }}>
              <Box sx={{ display: 'flex', flexWrap: 'wrap', alignItems: 'center' }}>
                <img src={logo} alt="...loading" style={{ width: '110px' }} href="/" />
              </Box>
              <IconButton
                size="large"
                aria-label="account of current user"
                aria-controls="menu-appbar"
                aria-haspopup="true"
                onClick={handleOpenNavMenu}
                color="inherit"
                sx={{
                  display: 'flex',
                  flexGrow: 1,
                  justifyContent: 'end',
                  ml: 2, // Increased spacing
                }}
              >
                <MenuIcon />
              </IconButton>

              <Menu
                id="menu-appbar"
                anchorEl={anchorElNav}
                anchorOrigin={{
                  vertical: 'bottom',
                  horizontal: 'left',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'left',
                }}
                open={Boolean(anchorElNav)}
                onClose={handleClose}
                sx={{
                  display: { xs: 'block', md: 'none' },
                  justifyContent: 'end',
                }}
              >
                <FacebookIcon sx={{ cursor: 'pointer', color: '#018EA2', m:'0px' }} /> 
                <InstagramIcon sx={{ cursor: 'pointer', color: '#018EA2', m:'0px' }} /> 
                <WhatsAppIcon sx={{ cursor: 'pointer', color: '#018EA2', m:'0px' }} /> 
                <LanguageIcon sx={{ cursor: 'pointer', color: '#606060', m:'0px' }} /> 

                {pages.map((page) => (
                  <MenuItem key={page} onClick={() => handleCloseNavMenu(page)}>
                    <Typography textAlign="center">{page}</Typography>
                  </MenuItem>
                ))}
              </Menu>
            </Box>
            <Box
              sx={{
                flexGrow: 1,
                display: { xs: 'none', md: 'flex' },
                flexWrap: 'wrap',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <Box
                sx={{
                  display: 'flex',
                  flexWrap: 'wrap',
                  justifyContent: 'center',
                  alignItems: 'center',
                  gap: matches ? '40px' : '20px', 
                }}
              >
                {pages.map((page) => (
                  <Button
                    key={page}
                    onClick={() => {
                      handleCloseNavMenu(page);
                    }}
                    sx={{
                      my: 2,
                      color: '#606060',
                      display: 'block',
                      fontSize: '10px',
                      position: 'relative',
                      overflow: 'hidden',
                    }}
                  >
                    {page}
                  </Button>
                ))}
                <Button
                  sx={{
                    bgcolor: '#018EA2',
                    color: 'white',
                    borderRadius: '20px',
                    marginLeft: '40px',
                  }}
                >
                  Get In Touch!
                </Button>
                <FacebookIcon sx={{ cursor: 'pointer', color: '#018EA2', m:'0px' }} /> 
                <InstagramIcon sx={{ cursor: 'pointer', color: '#018EA2', m:'0px' }} /> 
                <WhatsAppIcon sx={{ cursor: 'pointer', color: '#018EA2', m:'0px' }} /> 
                <a onClick={()=>{setLangMenu(!langMenu)}}>
                <LanguageIcon sx={{ cursor: 'pointer', color: '#606060', m:'0px' }} /> 
                </a>
                <Menu sx={{ mt: '65px',marginLeft:'-90px' }}
              anchorEl={langMenu}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={Boolean(langMenu)}
              onClose={handleCloseLangMenu}>
            <MenuItem>
              <Button sx={{ color:'black' }}
               onClick={() =>{ handleEn() }}>{t('English')}</Button>
              </MenuItem>
              <MenuItem>
              <Button sx={{ color:'black' }}
              onClick={() =>{ handleAr() }}>{t('Arabic')}</Button>
              </MenuItem>
        </Menu>
              </Box>
            </Box>
          </Toolbar>
        </Container>
      </AppBar>
    </>
  );
}

export default Navbar;
