import { Box, Accordion, AccordionSummary, AccordionDetails, Typography, Container } from '@mui/material';
import React, { useState } from 'react';
import { useTranslation } from "react-i18next";
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';

export default function FAQs() {
  const { t } = useTranslation();
  const [questions, setQuestions] = useState([
    {
      "Question": "What are the cars specifications",
      "answer": "1)-Classic cars are cars for daily use and are considered the most widely used category in cities   2)-VIP cars are modern cars for use within cities or between governorates. They are comfortable, air-conditioned cars suitable for businessmen and special occasions 3)-VAN cars are a modern, air-conditioned car with a capacity of up to 11 passengers, suitable for companies, institutions, special events, and tourist trips 4)-Mini-VAN cars with a capacity of 7 passengers are used within cities and for transporting employees. It is considered the most economical category if the number of passengers is (5-7) passengers."
    },
    {
      "Question": "What types of cars are available",
      "answer": "Classic – VIP – VAN – mini-VAN"
    }
  ]);

  const toggleQuestion = (index) => {
    const newQuestions = [...questions];
    newQuestions[index].isOpen = !newQuestions[index].isOpen;
    setQuestions(newQuestions);
  };

  return (
    <div sx={{ marginBottom: '30px' }}>
      <Box sx={{
        backgroundColor: '#F5C660', width: '100%', justifyContent: 'center',
        alignItems: 'center', paddingBottom: '60px'
      }}>
        <Typography sx={{
          display: 'flex', flexWrap: 'wrap', justifyContent: 'center', alignItems: 'center',
          fontWeight: 'bold', fontSize: '30px', color: '#7D4896', marginBottom: '20px'
        }}>
          {t('FAQs')}
        </Typography>


        {questions.map((q, index) => (
          <Accordion
            key={index}
            sx={{
              backgroundColor: 'transparent',
              '&:before': { display: 'none' },
              borderBottom: '1px solid #7D4896',
              padding: '30px',
              '&:last-child': { borderBottom: '5px solid #7D4896', width: '80%' }
            }}
            elevation={0}
            disableGutters
            expanded={q.isOpen}
            onChange={() => toggleQuestion(index)}
          >
            <AccordionSummary>
              {q.isOpen ? (
                <RemoveIcon sx={{ color: '#7D4896', marginRight: '20px', marginTop: '5px' }} />
              ) : (
                <AddIcon sx={{ color: '#7D4896', marginRight: '20px', marginTop: '5px' }} />
              )}
              <Typography sx={{ color: '#7D4896', fontSize: '20px', fontWeight: 'bold' }}>
                {q.Question}
              </Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography sx={{ color: '#7D4896', fontSize: '16px' }}>
                {q.answer}
              </Typography>
            </AccordionDetails>
          </Accordion>
        ))}
      </Box>
    </div>
  );
}
